// Extract job listings from UL Cyberpark
const html = $input.item.json.data;
const jobs = [];

// Regex to find an entire job row (everything between <tr> and </tr>)
// The 's' flag is important: it makes '.' match newline characters.
const jobRowRegex = /<tr.*?>(.*?)<\/tr>/gs;

// Regexes to find specific data *within* a single row
const titleRegex = /<a class="btn-1 btn-f-style cls_for_style"[^>]*>(.*?)<\/a>/;
const dateRegex = /closing date: ([\d-]+)/;
const companyRegex = /<a class="btn-1 btn-f-style cls_for_style" href=.*?>(.*?)<\/a>/;
const jobLinkRegex = /href="https:\/\/www\.ulcyberpark\.com\/jobs\/job_vacancy\?job_id=(\d+)"/;

let rowMatch;

// Loop through every job row found in the HTML
while ((rowMatch = jobRowRegex.exec(html)) !== null) {

  // Get the content inside the <tr> tags
  const rowContent = rowMatch[1];

  // Run the specific regexes on this row's content
  const titleMatch = rowContent.match(titleRegex);
  const dateMatch = rowContent.match(dateRegex);
  const companyMatch = rowContent.match(companyRegex);
  const jobLinkMatch = rowContent.match(jobLinkRegex);

  // We must have a job link/ID to consider it a valid job
  if (jobLinkMatch && jobLinkMatch[1]) {
    const jobId = jobLinkMatch[1];

    // Extract data, providing 'N/A' as a fallback if not found
    const title = titleMatch ? titleMatch[1].trim() : 'N/A';
    const company = companyMatch ? companyMatch[1].trim() : 'N/A';
    const closing_date = dateMatch ? dateMatch[1].trim() : 'N/A';
    const url = `https://www.ulcyberpark.com/jobs/job_vacancy?job_id=${jobId}`;

    // Add the structured job object to our array
    jobs.push({
      id: `ulcyber_${jobId}`,
      title: title,
      company: company,
      closing_date: closing_date,
      url: url,
      source: 'UL Cyberpark'
    });
  }
}

return jobs.map(job => ({ json: job }));

